INSERT INTO `products` VALUES (1, 1, 'Dầu gội X-Men FOR BOSS', 'Dầu gội hương nước hoa mạnh mẽ', 'images/product/1/product-img.png', 'xmen', '<h4>Dầu gội nước hoa X-Men for Boss</h4>\r\n<p>Với c&ocirc;ng thức chuy&ecirc;n biệt d&agrave;nh cho nam giới Dầu gội nước hoa X-Men for Boss Intense 380g gi&uacute;p gội sạch dầu nhờn v&agrave; bụi bẩn tr&ecirc;n da đầu. Kết hợp th&agrave;nh phần cao cấp gỗ Đ&agrave;n Hường v&agrave; Hổ Ph&aacute;ch tạo n&ecirc;n hương nước hoa trầm đầy nội lực d&agrave;nh cho người đ&agrave;n &ocirc;ng th&agrave;nh c&ocirc;ng.</p>\r\n<p>Tạo n&ecirc;n nốt hương trầm đầy nội lực.</p>\r\n<p>L&agrave;m sạch, nu&ocirc;i dưỡng t&oacute;c v&agrave; da đầu.</p>\r\n<p>Th&agrave;nh phần gỗ Đ&agrave;n Hương v&agrave; Hổ Ph&aacute;ch.</p>\r\n<p>Chai nhỏ tiết kiệm diện t&iacute;ch, thuận tiện khi đem theo c&ocirc;ng t&aacute;c/du lịch.</p>\r\n<p>N&ecirc;n d&ugrave;ng trọn bộ sản phẩm để to&agrave;n bộ cơ thể c&oacute; c&ugrave;ng hương thơm lịch l&atilde;m.</p>');
INSERT INTO `products` VALUES (18, 1, 'Dầu Gội Cao Cấp Cho Nam Romano VIP', 'Dầu Gội Cao Cấp Cho Nam Romano VIP', 'images/product/18/product-img.png', 'romano', '<h4>Dầu Gội Cao Cấp Cho Nam Romano VIP</h4>\r\n<p>C&oacute; c&ocirc;ng thức d&agrave;nh ri&ecirc;ng cho người đ&agrave;n &ocirc;ng c&aacute; t&iacute;nh gi&uacute;p m&aacute;i t&oacute;c bạn sẽ trở n&ecirc;n mượt m&agrave;, dễ d&agrave;ng v&agrave;o nếp v&agrave; kh&ocirc;ng c&ograve;n dấu hiệu của g&agrave;u, đem lại cảm gi&aacute;c sảng kho&aacute;i c&ugrave;ng m&ugrave;i hương nam t&iacute;nh lưu lại tr&ecirc;n t&oacute;c sau khi sử dụng.</p>\r\n<p>Romano VIP l&agrave; sự kết hợp tinh tế giữa phong c&aacute;ch lịch l&atilde;m c&ugrave;ng chất trẻ trung hiện đại với hương nước hoa nam t&iacute;nh, mạnh mẽ cho ph&aacute;i mạnh.</p>');
INSERT INTO `products` VALUES (19, 1, 'Dầu gội nước hoa X-Men Metal', 'Hương Citrus tươi mát cùng công thức 3Dmatrix giúp cắt sạch gàu', 'images/product/19/product-img.png\r\n', 'xmen', '<h4>Dầu gội nước hoa X-Men Metal</h4>\r\n<p>Hương Citrus tươi m&aacute;t c&ugrave;ng c&ocirc;ng thức 3D matrix gi&uacute;p cắt sạch g&agrave;u, X-men Kim Sạch g&agrave;u lịch l&atilde;m cho m&aacute;i t&oacute;c vẻ lịch l&atilde;m đầy uy lực, để bạn c&oacute; niềm tin th&eacute;p xuy&ecirc;n ph&aacute; mọi r&agrave;o cản.</p>\r\n<p>C&aacute;ch d&ugrave;ng: Thoa đều dầu gội l&ecirc;n t&oacute;c ướt. Xoa nhẹ nh&agrave;ng rồi xả sạch với nước.</p>\r\n<p>Th&agrave;nh phần: Water, Sodium Laureth Sulfate, Dimethiconol, Dimethicane, Cocamidopropyl Betaine, Perfume, Zinc Pyrithione, Carbomer, Menthol, Sodium PCA, Sodium Hydroxide, Guar Hydraxypropyttrinonium Chloride, Citric Acid, TEA-Dodecylbenzenesulfonate, Polyquaternium-10, Castoryl Maleate, C12-15 Pareth-3, Tocopheryl Acetate, Betaine, Sorbitol, Serine, Glycine, Glutamic Acid, Alanine, Lysine, Arginine, Threonine, Proline, Sodium Cumenesulfonate, Sodium Chloride, Methylchloroisothiazolinone, Methylisothiazolone, CI 42090.</p>');
INSERT INTO `products` VALUES (20, 1, 'Dầu Gội Bùn Khoáng Sạch Gàu Mát Lạnh Nivea Men Ice Mud Anti Dandruf Shampoo', 'Hương bạc hà tươi mát, tạo cảm giác thư giãn, sảng khoái', 'images/product/20/product-img.png', 'nivea', '<h4>Dầu Gội B&ugrave;n Kho&aacute;ng Sạch G&agrave;u M&aacute;t Lạnh Nivea Men Ice Mud Anti Dandruf Shampoo</h4>\r\n<p>C&ocirc;ng thức chứa kho&aacute;ng chất từ b&ugrave;n m&aacute;t lạnh, cho khả năng l&agrave;m sạch mạnh mẽ, xo&aacute;y bay bụi bẩn tr&ecirc;n da đầu. Sản phẩm c&oacute; hương bạc h&agrave; tươi m&aacute;t, tạo cảm gi&aacute;c thư gi&atilde;n, sảng kho&aacute;i khi sử dụng. Ngo&agrave;i ra, c&ograve;n mang đến cho nam giới một trải nghiệm ho&agrave;n to&agrave;n kh&aacute;c.</p>\r\n<p>Dầu gội đầu với c&ocirc;ng thức b&ugrave;n kho&aacute;ng chứa ph&acirc;n tử si&ecirc;u nhỏ hoạt động như c&aacute;c thỏi nam ch&acirc;m gi&uacute;p xo&aacute;y bay bụi bẩn, xoa dịu v&agrave; nu&ocirc;i dưỡng da đầu, ngăn nhờn ngứa cả ng&agrave;y. Loại sạch vảy g&agrave;u từ gốc, nu&ocirc;i dưỡng da đầu v&agrave; ngăn ngừa g&agrave;u quay trở lại. Tạo cảm gi&aacute;c m&aacute;t lạnh với m&ugrave;i hương bạc h&agrave; sản kho&aacute;i.</p>');
INSERT INTO `products` VALUES (21, 1, 'Dầu Gội Nam Kerasys Homme Deep Cleansing Cool Shampoo ', 'Thành phần 100% thiên nhiên với tinh chất bạc hà', 'images/product/21/product-img.png\r\n', 'kerasys', '<h4>Dầu Gội Nam Kerasys Homme Deep Cleansing Cool Shampoo</h4>\r\n<p>C&oacute; th&agrave;nh phần 100% thi&ecirc;n nhi&ecirc;n với tinh chất bạc h&agrave;, được kiểm nghiệm theo ti&ecirc;u chuẩn của Climbasol v&agrave; Sodiumsalicylate, kh&ocirc;ng chứa h&oacute;a chất độc hại cho sức khỏe. Sản phẩm gi&uacute;p gội sạch ho&agrave;n to&agrave;n nhưng kh&ocirc;ng hề g&acirc;y ngứa hay k&iacute;ch ứng da đầu. Tinh chất bạc h&agrave; m&aacute;t lạnh mang lại cho bạn cảm gi&aacute;c thư th&aacute;i, dịu m&aacute;t cho ng&agrave;y h&egrave; năng động.</p>\r\n<p>Th&ocirc;ng qua những hạt si&ecirc;u vi thẩm thấu mạnh mẽ v&agrave;o từng sợi t&oacute;c gi&uacute;p loại bỏ ngay những bụi bẩn, c&aacute;c chất nhờn, mồ h&ocirc;i đầu, mảng b&aacute;m g&agrave;u. M&aacute;i t&oacute;c của bạn sẽ trở n&ecirc;n &oacute;ng mượt, giữ nếp cả ng&agrave;y d&agrave;i khi sử dụng với keo tạo kiểu.</p>');
INSERT INTO `products` VALUES (22, 1, 'Dầu Gội Ngăn Lão Hóa Tóc Oxy Prime RMV-O-Pr-Sh', 'Chiết xuất từ thảo mộc thiên nhiên, không chứa Silicone, Sulfate', 'images/product/22/product-img.png\r\n', 'Oxy', '<h4>Dầu Gội Ngăn L&atilde;o H&oacute;a T&oacute;c Oxy Prime RMV-O-Pr-Sh</h4>\r\n<p>C&oacute; chiết xuất từ thảo mộc thi&ecirc;n nhi&ecirc;n, kh&ocirc;ng chứa Silicone, Sulfate &ndash; t&aacute;c nh&acirc;n g&acirc;y rụng t&oacute;c.</p>\r\n<p>Kết hợp với c&aacute;c dưỡng chất gi&uacute;p t&oacute;c ph&aacute;t triển chắc khỏe gi&uacute;p ngăn l&otilde;a h&oacute;a t&oacute;c v&agrave; gi&uacute;p t&oacute;c giảm g&atilde;y rụng, ph&aacute;t triển nhanh, chắc khỏe v&agrave; s&aacute;ng b&oacute;ng.</p>\r\n<p>Sản phẩm sử dụng chai v&ograve;i tiện dụng, gi&uacute;p bạn dễ d&agrave;ng lấy ra lượng dầu gội th&iacute;ch hợp để sử dụng.</p>\r\n<p>Dầu Gội Ngăn L&atilde;o H&oacute;a T&oacute;c Oxy Prime RMV-O-Pr-Sh đến từ thương hiệu nổi tiếng, uy t&iacute;n từ Nhật Bản</p>');
INSERT INTO `products` VALUES (23, 1, 'Dầu Gội Romano Deluxe Classic', 'Ứng dụng công thức cải tiến chứa Pro-Vitamin B5 cho mái tóc mềm mại, chắc khỏe.', 'images/product/23/product-img.png', 'romano', '<h4>Dầu Gội Romano Deluxe Classic</h4>\r\n<p>D&agrave;nh cho ph&aacute;i mạnh ứng dụng c&ocirc;ng thức cải tiến chứa Pro-Vitamin B5 cho m&aacute;i t&oacute;c mềm mại, chắc khỏe.</p>\r\n<p>Ngo&agrave;i ra, hương thơm độc đ&aacute;o, nam t&iacute;nh từ Romano sẽ mang đến cho bạn một phong c&aacute;ch lịch l&atilde;m v&agrave; quyến rũ.</p>\r\n<p>Dầu gội với c&ocirc;ng thức cải tiến gi&uacute;p l&agrave;m sạch da đầu hiệu quả v&agrave; v&agrave; mang đến cho bạn một m&aacute;i t&oacute;c mềm mượt v&agrave; khỏe mạnh cả ng&agrave;y d&agrave;i với th&agrave;nh phần Pro Vitamin B5.</p>\r\n<p>Romano c&oacute; hương thơm sảng kho&aacute;i, m&aacute;t lạnh v&agrave; nam t&iacute;nh sẽ cho bạn phong c&aacute;ch trẻ trung, năng động v&agrave; quyến rũ.</p>\r\n<p>Sản phẩm d&agrave;nh cho mọi loại t&oacute;c.</p>');
INSERT INTO `products` VALUES (24, 2, 'Dầu Gội Trà Xanh Phục Hồi Và Ngăn Rụng Tóc Follow Me Hair Rivitalizer', 'Giữ ẩm cho tóc, bổ sung dưỡng chất và phục hồi mái tóc khô, hư tổn', 'images/product/24/product-img.png', 'rivitalizer', '<h4>Dầu Gội Tr&agrave; Xanh Phục Hồi V&agrave; Ngăn Rụng T&oacute;c Follow Me Hair Rivitalizer</h4>\r\n<p>Chiết xuất từ tr&agrave; xanh, chiết xuất mầm l&uacute;a m&igrave;, hạt nho cung cấp v&agrave; giữ ẩm cho t&oacute;c, bổ sung dưỡng chất v&agrave; phục hồi m&aacute;i t&oacute;c kh&ocirc;, hư tổn do t&aacute;c nh&acirc;n sấy, duỗi, uốn, nhuộm.</p>\r\n<p>Đặc biệt, sản phẩm nhẹ nh&agrave;ng gội sạch chất nhờn, bụi bẩn đồng thời để c&aacute;c dưỡng chất dễ d&agrave;ng thấm s&acirc;u v&agrave;o t&oacute;c, nu&ocirc;i dưỡng v&agrave; bảo vệ t&oacute;c tr&aacute;nh g&atilde;y rụng.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, dầu gội mang m&ugrave;i hương m&aacute;t dịu của l&aacute; tr&agrave; xanh mang đến cho bạn cảm gi&aacute;c sảng kho&aacute;i cũng như tự tin tỏa hương thơm quyến rũ, dễ chịu tr&ecirc;n suối t&oacute;c &oacute;ng mượt.</p>\r\n<p>Sản phẩm được l&agrave;m từ những th&agrave;nh phần chiết xuất từ thi&ecirc;n nhi&ecirc;n kết hợp quy tr&igrave;nh kiểm tra nghi&ecirc;m ngặt kh&ocirc;ng g&acirc;y k&iacute;ch ứng da an to&agrave;n với người sử dụng.</p>\r\n<p>Thiết kế dạng chai c&oacute; v&ograve;i tiện lợi, chắc chắn gi&uacute;p bạn dễ d&agrave;ng mang theo mọi l&uacute;c, mọi nơi để bảo vệ, chăm s&oacute;c cho m&aacute;i t&oacute;c của m&igrave;nh.</p>');
INSERT INTO `products` VALUES (25, 2, 'Dầu Gội Phục Hồi Tóc Chiết Xuất Dầu Ngựa Nagano NG1043 ', 'Bảo vệ chống khô và gãy rụng cho tóc,chăm sóc chuyên sâu cho tóc hư tổn', 'images/product/25/product-img.png', 'nagano', '<h4>Dầu Gội Phục Hồi T&oacute;c Chiết Xuất Dầu Ngựa Nagano NG1043</h4>\r\n<p>Th&agrave;nh phần chủ yếu l&agrave; dầu ngựa dễ d&agrave;ng thẩm thấu s&acirc;u nu&ocirc;i dưỡng da đầu khỏe mạnh.</p>\r\n<p>Tinh chất dầu ngựa bổ sung dưỡng chất, cải tạo cấu tr&uacute;c b&ecirc;n trong của t&oacute;c, k&iacute;ch th&iacute;ch tuần ho&agrave;n m&aacute;u, gi&uacute;p t&oacute;c lu&ocirc;n chắc khỏe, b&oacute;ng đẹp.</p>\r\n<p>Bổ sung tăng cường c&aacute;c loại vitamin, protein c&aacute;m gạo, chiết xuất hoa đậu biếc&hellip; gi&uacute;p cải thiện c&aacute;c vấn đề về t&oacute;c như: hư tổn, g&agrave;u, ngứa, g&atilde;y rụng, xơ, chẻ ngọn&hellip; trả lại cho bạn một m&aacute;i t&oacute;c d&agrave;y, b&oacute;ng đẹp, bồng bềnh m&agrave; kh&ocirc;ng g&acirc;y bết d&iacute;nh.</p>\r\n<p>Chất chiết xuất từ dầu ngựa, protein c&aacute;m gạo, chiết xuất hoa đậu biếc, vitamin&hellip; dưỡng t&oacute;c b&oacute;ng mượt, chắc khỏe.</p>');
INSERT INTO `products` VALUES (26, 2, 'Dầu Gội Dr. Sante Argan Hair Phục Hồi Tóc Hư Tổn ', 'Giảm thiểu và khôi phục tối đa sợi tóc bị khô, chẻ ngọn', 'images/product/26/product-img.png', 'argan', '<h4>Dầu Gội Dr. Sante Argan Hair Phục Hồi T&oacute;c Hư Tổn</h4>\r\n<p>Được đặc chế theo c&ocirc;ng nghệ ri&ecirc;ng của h&atilde;ng mỹ phẩm ch&acirc;u &Acirc;u nổi tiếng - Dr.Sante d&agrave;nh ri&ecirc;ng cho m&aacute;i t&oacute;c đ&atilde; bị hư tổn. Với c&aacute;c dưỡng chất từ dầu Argan v&agrave; Keratin gi&uacute;p thấm s&acirc;u, giảm thiểu t&oacute;c hư tổn v&agrave; xoăn cứng, dầu gội sẽ mang đến cho bạn m&aacute;i t&oacute;c b&oacute;ng khỏe từ gốc đến ngọn.</p>\r\n<p>Argan Oil: th&agrave;nh phần dầu Argan từ l&acirc;u đ&atilde; nổi tiếng trong việc dưỡng ẩm s&acirc;u, đặc biệt phục hồi v&agrave; tăng cường dinh dưỡng cho từng sợi t&oacute;c để giảm thiểu tối đa t&oacute;c chẻ ngọn, cải thiện độ sang, b&oacute;ng của t&oacute;c.</p>\r\n<p>Keratin: l&agrave; một trong những th&agrave;nh phần cơ bản của m&aacute;i t&oacute;c, tuy nhi&ecirc;n, theo thời gian, dưới t&aacute;c động của m&ocirc;i trường, t&oacute;c sẽ bị mất dần lượng Keratin. Ch&iacute;nh v&igrave; vậy, việc trong sản phẩm c&oacute; chứa th&agrave;nh phần Keratin sẽ gi&uacute;p bổ sung lượng chất đạm tự nhi&ecirc;n bị mất, phục hồi m&aacute;i t&oacute;c hư tổn. Keratin sẽ th&acirc;m nhập v&agrave;o từng sợi t&oacute;c, l&agrave;m đầy c&aacute;c vết nh&aacute;m, kh&ocirc;, xơ tr&ecirc;n t&oacute;c, gi&uacute;p t&oacute;c trở n&ecirc;n b&oacute;ng mượt hơn, đồng thời ngăn ngừa g&atilde;y rụng v&agrave; chẻ ngọn.</p>');
INSERT INTO `products` VALUES (27, 2, 'Kem Phục Hồi Tóc Hư Tổn MH Natural Haircare', 'Dưỡng tóc mềm mại, chống rụng tóc, giúp mọc tóc, chống gàu', 'images/product/27/product-img.png', 'haircare', '<h4>Kem Phục Hồi T&oacute;c Hư Tổn MH Natural Haircare</h4>\r\n<p>Chiết xuất từ những nguy&ecirc;n liệu từ thi&ecirc;n nhi&ecirc;n như dầu dừa, dầu castor, bơ hạt mỡ, vitamin E tinh dầu dưỡng v&agrave; mọc t&oacute;c Rosemary.</p>\r\n<p>Sản phẩm gi&uacute;p dưỡng cho m&aacute;i t&oacute;c mềm mại, chống rụng t&oacute;c, gi&uacute;p mọc t&oacute;c nhanh v&agrave; hạn chế g&agrave;u.</p>\r\n<p>Hương thơm nhẹ dịu suốt cả ng&agrave;y.</p>');
INSERT INTO `products` VALUES (28, 2, 'Dầu gội bưởi & Bồ Kết Herbario giảm rụng tóc, phục hồi tóc ', 'Giảm rụng tóc, cung cấp dưỡng chất nuôi dưỡng từ gốc đến ngọn, phục hồi tóc hư tổn sâu, giúp tóc suôn mượt tự nhiên', 'images/product/28/product-img.png', 'herbario', '<h4>Dầu gội bưởi &amp; Bồ Kết Herbario giảm rụng t&oacute;c, phục hồi t&oacute;c</h4>\r\n<p>Dầu gội vỏ bưởi &amp; bồ kết HERBARIO gi&uacute;p giảm rụng t&oacute;c*, nu&ocirc;i dưỡng t&oacute;c từ s&acirc;u b&ecirc;n trong, cung cấp dưỡng chất gi&uacute;p t&oacute;c chắc khỏe, phục hồi t&oacute;c hư tổn do t&aacute;c động của m&ocirc;i trường v&agrave; h&oacute;a chất, gi&uacute;p t&oacute;c lu&ocirc;n khỏe mạnh, su&ocirc;n mượt v&agrave; mềm mại.</p>\r\n<p>D&ugrave;ng cho c&aacute;c trường hợp rụng t&oacute;c nhiều v&agrave; sau rụng t&oacute;c do c&aacute;c nguy&ecirc;n nh&acirc;n: thay đổi nội tiết, l&atilde;o h&oacute;a, sử dụng c&aacute;c loại thuốc, h&oacute;a chất, thiếu dinh dưỡng, l&agrave;m việc căng thẳng, thay đổi nguồn nước, hoặc t&oacute;c rụng nhiều kh&ocirc;ng c&oacute; nguy&ecirc;n nh&acirc;n; những người t&oacute;c thưa, xơ, chẻ ngọn, t&oacute;c c&oacute; nhiều dầu, người c&oacute; nguy cơ h&oacute;i đầu v&agrave; bệnh nh&acirc;n rụng t&oacute;c sau thời gian h&oacute;a trị, xạ trị ung thư.</p>');
INSERT INTO `products` VALUES (29, 2, 'Tinh Chất Phục Hồi Tóc Hư Tổn Từ Olive Aspasia Oilve Essence ', 'Khôi phục lại sức sống và dinh dưỡng cho tóc yếu, tóc chẻ ngọn, hương thơm quyến rũ  và đem lại sự mượt mà óng mượt cho tóc', 'images/product/29/product-img.png', 'aspasia', '<h4>Tinh Chất Phục Hồi T&oacute;c Hư Tổn Từ Olive Aspasia Oilve Essence</h4>\r\n<p>Th&agrave;nh phần si&ecirc;u đậm đặc, chiết xuất từ tr&aacute;i Olive c&ugrave;ng h&agrave;ng loạt c&aacute;c Vitamin, c&aacute;c acid b&eacute;o dưỡng t&oacute;c gi&uacute;p cho t&oacute;c mềm mại v&agrave; giảm xơ rối tức th&igrave;. Dưỡng chất trong Olive c&ugrave;ng với h&agrave;m lượng dưỡng ẩm cho t&oacute;c kh&ocirc;, xơ gi&uacute;p cung cấp dinh dưỡng cho m&aacute;i t&oacute;c yếu, chẻ ngọn một c&aacute;ch hiệu quả v&agrave; l&acirc;u d&agrave;i. Ngo&agrave;i ra,hương thơm quyến rũ lưu lại tr&ecirc;n t&oacute;c suốt cả ng&agrave;y v&agrave; đem lại sự mượt m&agrave; &oacute;ng mượt cho t&oacute;c.</p>\r\n<p>Tinh chất dưỡng t&oacute;c Aspasia Olive Essence với chiết xuất tr&aacute;i olive gi&uacute;p phục hồi sức sống v&agrave; dinh dưỡng cho m&aacute;i t&oacute;c kh&ocirc; v&agrave; chẻ ngọn.</p>\r\n<p>Hương thơm dịu nhẹ quyến rũ lưu lại tr&ecirc;n t&oacute;c suốt cả ng&agrave;y v&agrave; đem lại sự &oacute;ng mượt cho m&aacute;i t&oacute;c.</p>');
INSERT INTO `products` VALUES (30, 2, 'Dầu Xả Phục Hồi Và Bảo Vệ Tóc Chiết Xuất Olive Và Argan ', 'Giúp tóc chắc khỏe, cân bằng độ ẩm và bóng mượt, làm mềm và mượt tóc, giúp tóc nhanh vào nếp', 'images/product/30/product-img.png', 'olive', '<h4>Dầu Xả Phục Hồi V&agrave; Bảo Vệ T&oacute;c Chiết Xuất Olive V&agrave; Argan</h4>\r\n<p>Chứa h&agrave;m lượng lớn oleic axit gi&uacute;p củng cố v&agrave; bảo vệ t&oacute;c bằng c&aacute;ch li&ecirc;n kết lại c&aacute;c tế b&agrave;o gốc tự nhi&ecirc;n.</p>\r\n<p>Kết hợp với h&agrave;m lượng cao Protein Complex tạo n&ecirc;n bởi protein của đậu n&agrave;nh, l&uacute;a mạch v&agrave; ng&ocirc;, dầu xả t&oacute;c olive v&agrave; argan gi&uacute;p t&oacute;c chắc khỏe, c&acirc;n bằng độ ẩm v&agrave; b&oacute;ng mượt.</p>\r\n<p>Tinh dầu Olive Gi&uacute;p l&agrave;m mềm v&agrave; mượt t&oacute;c, gi&uacute;p t&oacute;c nhanh v&agrave;o nếp, thấm s&acirc;u k&iacute;ch th&iacute;ch t&oacute;c nhanh mọc d&agrave;i, dưỡng ẩm tr&aacute;nh bong tr&oacute;c da đầu. Trị g&agrave;u, giảm kh&ocirc; da đầu, c&oacute; thể trị nấm da đầu giảm ngứa do gầu g&acirc;y ra. C&aacute;c dưỡng chất trong dầu oliu c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch ch&acirc;n t&oacute;c, gi&uacute;p t&oacute;c nhanh mọc d&agrave;i, giảm g&atilde;y rụng, tạo độ d&agrave;y cho m&aacute;i t&oacute;c.</p>');
INSERT INTO `products` VALUES (31, 3, 'Gel Tạo Tóc Gatsby Wg Uh-Gel Siêu Cứng Level 9 ', 'Tạo kiểu tóc siêu cứng ấn tượng, tính năng độc đáo giữ được độ cứng cho tóc', 'images/product/31/product-img.png', 'gatsby', '<h4>Gel Tạo T&oacute;c Gatsby Wg Uh-Gel Si&ecirc;u Cứng Level 9</h4>\r\n<p>Tạo kiểu t&oacute;c si&ecirc;u cứng ấn tượng</p>\r\n<p>T&iacute;nh năng độc đ&aacute;o giữ được độ cứng cho t&oacute;c.</p>\r\n<p>Bổ sung t&iacute;nh năng kh&ocirc;ng bết d&iacute;nh</p>\r\n<p>Gi&uacute;p tạo kiểu đa dạng</p>\r\n<p>Giữ độ b&oacute;ng tốt</p>');
INSERT INTO `products` VALUES (32, 3, 'Wax Tạo Kiểu Tóc Amby London Hex Twisted Fibre', 'Tạo kiểu, làm bóng, giữ nếp và bảo vệ tóc, khô nhanh, không gây bết dính', 'images/product/32/product-img.png', 'amby', '<h4>Wax Tạo Kiểu T&oacute;c Amby London Hex Twisted Fibre</h4>\r\n<p>S&aacute;p tạo kiểu giữ nếp tốt, hương thơm nhẹ nh&agrave;ng, dễ d&agrave;ng gội sạch v&agrave; kh&ocirc;ng l&agrave;m nặng t&oacute;c.</p>\r\n<p>Wax với c&ocirc;ng thức độc đ&aacute;o gi&uacute;p t&oacute;c dễ v&agrave;o nếp đồng thời tạo lớp m&agrave;ng bảo vệ t&oacute;c khỏi c&aacute;c t&aacute;c hại của m&ocirc;i trường v&agrave; &aacute;nh nắng mặt trời, đem lại m&aacute;i t&oacute;c phong c&aacute;ch, c&aacute; t&iacute;nh như &yacute; muốn.</p>\r\n<p>Sản phẩm được sản xuất tại Malaysia với c&aacute;c th&agrave;nh phần an to&agrave;n với m&aacute;i t&oacute;c người sử dụng.</p>');
INSERT INTO `products` VALUES (33, 3, 'Gel Tạo Kiểu Tóc Goodlook Hard Hair Gel ', 'Bảo vệ tóc, giúp tóc bóng mượt, hương thơm nam tính, phù hợp với mọi loại tóc', 'images/product/33/product-img.png', 'goodluck', '<h4>Gel Tạo Kiểu T&oacute;c Goodlook Hard Hair Gel</h4>\r\n<p>Được sản xuất từ th&agrave;nh phần gi&agrave;u protein, chiết xuất hạnh nh&acirc;n, tiền Vitamin B5 v&agrave; hoạt chất lọc tia UV.</p>\r\n<p>Gel gi&uacute;p tạo kiểu t&oacute;c đứng v&agrave; giữ nếp l&acirc;u, hương thơm nam t&iacute;nh, ph&ugrave; hợp với mọi loại t&oacute;c.</p>\r\n<p>Kh&ocirc;ng chỉ vậy, gel c&ograve;n gi&uacute;p bảo vệ t&oacute;c trước t&aacute;c hại của bụi bẩn, &ocirc; nhiễm v&agrave; tia bức xạ mặt trời, gi&uacute;p t&oacute;c lu&ocirc;n khỏe mạnh, b&oacute;ng mượt v&agrave; dễ chải.</p>');
INSERT INTO `products` VALUES (34, 3, 'Gel Gatsby Vuốt Tóc Siêu Cứng SH', 'Nhanh khô và tạo kiểu tóc không dính và sáng bóng, chứa tác nhân tạo kiểu chống ẩm có khả năng chống mồ hôi và độ ẩm', 'images/product/34/product-img.png', 'gatsby', '<h4>Gel Gatsby Vuốt T&oacute;c Si&ecirc;u Cứng SH</h4>\r\n<p>Nhanh kh&ocirc; v&agrave; tạo kiểu t&oacute;c kh&ocirc;ng d&iacute;nh v&agrave; s&aacute;ng b&oacute;ng. Chứa t&aacute;c nh&acirc;n tạo kiểu chống ẩm c&oacute; khả năng chống mồ h&ocirc;i v&agrave; độ ẩm.</p>\r\n<p>Dễ d&agrave;ng để restyle với nước.</p>\r\n<p>L&agrave;m mới hương thơm cam qu&yacute;t v&agrave; chứa pro-vitamin B5.</p>');
INSERT INTO `products` VALUES (35, 3, 'Gel Tạo Kiểu Tóc Goodlook Wet Hard Styling Gel', 'Bảo vệ tóc, giúp tóc bóng mượt, hương thơm nam tính, phù hợp với mọi loại tóc', 'images/product/35/product-img.png', 'goodluck', '<h4>Gel Tạo Kiểu T&oacute;c Goodlook Wet Hard Styling Gel</h4>\r\n<p>Được sản xuất từ th&agrave;nh phần gi&agrave;u vitamin E, chiết xuất hạnh nh&acirc;n, tiền Vitamin B5 v&agrave; hoạt chất lọc tia UV.</p>\r\n<p>Gel gi&uacute;p tạo kiểu t&oacute;c đứng v&agrave; giữ nếp l&acirc;u, hương thơm nam t&iacute;nh, ph&ugrave; hợp với mọi loại t&oacute;c.</p>\r\n<p>Kh&ocirc;ng chỉ vậy, gel c&ograve;n gi&uacute;p bảo vệ t&oacute;c trước t&aacute;c hại của bụi bẩn, &ocirc; nhiễm v&agrave; tia bức xạ mặt trời, gi&uacute;p t&oacute;c lu&ocirc;n khỏe mạnh, b&oacute;ng mượt v&agrave; dễ chải.</p>');
INSERT INTO `products` VALUES (36, 3, 'Gel vuốt tóc Romano Classic Siêu cứng', 'Giữ nếp siêu cứng, hương thơm nam tính, giúp tóc bóng khỏe, phù hợp với mọi loại tóc', 'images/product/36/product-img.png', 'romano', '<h4>Gel vuốt t&oacute;c Romano Classic Si&ecirc;u cứng</h4>\r\n<p>Giữ nếp si&ecirc;u cứng</p>\r\n<p>Gi&uacute;p t&oacute;c b&oacute;ng khỏe</p>\r\n<p>Ph&ugrave; hợp với mọi loại t&oacute;c</p>\r\n<p>Gi&uacute;p tạo những kiểu t&oacute;c ấn tượng với độ giữ nếp cứng. Gel tạo kiểu t&oacute;c Romano Classic si&ecirc;u cứng với c&ocirc;ng thức cải thiện gi&agrave;u chất giữ ẩm gi&uacute;p cho t&oacute;c b&oacute;ng khỏe. Kh&ocirc;ng l&agrave;m d&iacute;nh t&oacute;c, chứa th&agrave;nh phần ngăn tia UV. Hương thơm độc đ&aacute;o Romano Classic cho bạn một phong c&aacute;ch lịch l&atilde;m v&agrave; đầy quyến rũ. ​Hướng dẫn sử dụng Cho lượng gel vừa đủ v&agrave;o l&ograve;ng b&agrave;n tay, xoa đều v&agrave; vuốt l&ecirc;n t&oacute;c để tạo kiểu theo &yacute; muốn. Sau khi đội mũ bảo hiểm, l&agrave;m ẩm t&oacute;c để tạo lại kiểu t&oacute;c ưng &yacute;. Bảo quản Tr&aacute;nh để ở nhiệt độ cao. Đ&oacute;ng nắp sau khi sử dụng.</p>');
INSERT INTO `products` VALUES (37, 3, 'Gel X-Men Siêu Cứng & Bóng Tóc', 'Giải pháp giúp tóc không bị mất nếp, giúp giữ nếp siêu cứng ở cấp độ 6, dễ dàng tạo lại kiểu tóc sau khi đội mũ bảo hiểm', 'images/product/37/product-img.png', 'xmen', '<h4>Gel X-Men Si&ecirc;u Cứng &amp; B&oacute;ng T&oacute;c</h4>\r\n<p>Giải ph&aacute;p gi&uacute;p t&oacute;c kh&ocirc;ng bị mất nếp v&agrave; dễ d&agrave;ng tạo những kiểu t&oacute;c thật thời trang theo &yacute; muốn.</p>\r\n<p>Gel với hợp chất Copolymer v&agrave; Panthenol gi&uacute;p giữ nếp si&ecirc;u cứng ở cấp độ 6, dễ d&agrave;ng tạo lại kiểu t&oacute;c sau khi đội mũ bảo hiểm v&agrave; nu&ocirc;i dưỡng t&oacute;c chắc khỏe.</p>\r\n<p>Với X-men, bạn sẽ khẳng định được phong c&aacute;ch nam t&iacute;nh của m&igrave;nh với những kiểu t&oacute;c ấn tượng v&agrave; ho&agrave;n hảo nhất.</p>');
INSERT INTO `products` VALUES (38, 3, 'Gel X-Men Cứng Tóc', 'Giữ nếp tóc tự nhiên, ít bết dính, dễ dàng tạo kiểu tóc, hương gỗ trầm đỏ và hỗ phách, phù hợp với mọi loại tóc', 'images/product/38/product-img.png', 'xmen', '<h4>Gel X-Men Cứng T&oacute;c</h4>\r\n<p>Với c&ocirc;ng thức \"Strong Hold\" giữ l&acirc;u nếp t&oacute;c, giữ độ ẩm tự nhi&ecirc;n cho t&oacute;c v&agrave; kh&ocirc;ng g&acirc;y nhờn.</p>\r\n<p>Gel vuốt t&oacute;c Xmen gi&uacute;p bạn tạo những nếp t&oacute;c theo &yacute; muốn, giữ nếp t&oacute;c l&acirc;u v&agrave; cho m&aacute;i t&oacute;c b&oacute;ng mượt.</p>\r\n<p>Quan trọng hơn l&agrave; hương thơm độc đ&aacute;o, qu&yacute; ph&aacute;i của nước hoa Xmen Sport sẽ tạo cho bạn một phong c&aacute;ch, một c&aacute; t&iacute;nh đ&agrave;n &ocirc;ng đ&uacute;ng nghĩa.</p>\r\n<p>D&agrave;nh cho mọi loại t&oacute;c.</p>');

-- ----------------------------
-- Table structure for reservations
